"use strict";const items=document.querySelectorAll(".testimonials__item"),itemCount=items.length,nextItem=document.querySelector(".testimonials__btn--right"),previousItem=document.querySelector(".testimonials__btn--left");let count=0;function showNextItem(){items[count].classList.remove("testimonials__item--active"),count<itemCount-1?count++:count=0,items[count].classList.add("testimonials__item--active")}function showPreviousItem(){items[count].classList.remove("testimonials__item--active"),0<count?count--:count=itemCount-1,items[count].classList.add("testimonials__item--active")}nextItem.addEventListener("click",showNextItem),previousItem.addEventListener("click",showPreviousItem);